        "product_page_url": "https://www.shinanoya-tokyo.jp/shopdetail/000000018457/",
        "zoom_image_url": "https://makeshop-multi-images.akamaized.net/4708/itemimages/000000018457_Bsi3rZM.jpg",
        "image_url": "https://makeshop-multi-images.akamaized.net/4708/itemimages/0000000184572_U0zf75z.jpg",
        "thumbnail_image_url": "https://makeshop-multi-images.akamaized.net/4708/itemimages/0000000184573_37tfqgj.jpg",
        "sub_image1_url": "",
